mapper.select:
SELECT paged_images.* FROM ( SELECT ROWNUM rn, filtered_images.* FROM ( SELECT /*+ INDEX_DESC(board_image pk_boardimage) */ board_image.* FROM board_image WHERE board_id IS NULL
AND id < #{skippedId}
ORDER BY id DESC ) filtered_images WHERE ROWNUM <= ( (#{_page} + 1) * #{_pagesize} ) ) paged_images WHERE paged_images.rn > ( #{_page} * #{_pagesize} )

mapper.update:
UPDATE
	comments
SET
	like_count = #{realLikeCount}
WHERE
	id = #{commentId}
