mapper.delete:
{ 
	CALL
		DECLARE
			 v_board_category_code board.board_category_code%TYPE := NULL;
		BEGIN
			DELETE FROM
				board
    		WHERE 
        		board_idx = #{boardIdx} 
        	AND 
        		board_writer_idx = #{boardWriterIdx}
    		RETURNING 
        		board_category_code 
    		INTO
        		v_board_category_code;
        	#{boardCategoryCode, mode=OUT, jdbcType=NUMERIC, javaType=Integer} := v_board_category_code;
		END 
}
