mapper.update:

	SELECT 
		MAX(comment_idx) 
	FROM 
		"comment" 
	WHERE 
		comment_idx = #{commentIdx}

BEGIN
	IF #{commentIdx} IS NOT NULL THEN
		MERGE INTO 
			comment_likes
		USING
			DUAL
		ON (
			comment_idx = #{commentIdx}
			AND
			member_idx = #{memberIdx} 
		)
		WHEN MATCHED THEN
			DELETE
		WHEN NOT MATCHED THEN
			INSERT (
				comment_likes_idx,
				comment_idx,
				member_idx
			) VALUES (
				seq_comment_likes.NEXTVAL,
				#{commentIdx},
				#{memberIdx}
			);
	END IF;
END;
